using System.Collections.Generic;
using Assets.Scripts.Mechanics;
using Assets.Scripts.World;
using UnityEngine;

namespace Assets.Scripts.Engine
{
    public class GameRunner : MonoBehaviour
    {
        private int _fixedUpdateTicksCounter;
        [SerializeField] private readonly int _fixedUpdateTicksPerBeat = 31;

        private readonly List<CellularMechanics> _mechanics = new List<CellularMechanics>();
        public int BeatNo;
        
        public void ApplyMechanics(Board board)
        {
            foreach (var mechanic in _mechanics) mechanic.Apply(board);
        }

        private void Start()
        {
            //String map_test03 = "";
            var map_test02 =
                "";
            //String map_test01 = "";
            //String map_first_successful_test= "";
            Board.Current = BoardFactory.CreateFromString(map_test02);

            _mechanics.Add(new Wind());
            _mechanics.Add(new Gravity());
        }

        private void FixedUpdate()
        {
            if (_fixedUpdateTicksCounter == 0)
            {
                BeatNo++;
                ApplyMechanics(Board.Current);
            }

            _fixedUpdateTicksCounter = (_fixedUpdateTicksCounter + 1) % _fixedUpdateTicksPerBeat;
        }
    }
}